sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    if (newValue == '4a6f0a7b47033510eeeefb42e36d43f0') {
        g_form.addOption('incident_category', 'imaging_equipment', 'Imaging Equipment');
        g_form.addOption('subcategory', 'none', '-----None-----');
        g_form.addOption('subcategory', 'image_clarity_concerns', 'Image Clarity Concerns');
        g_form.addOption('subcategory', 'probe_malfunctions', 'Probe Malfunctions');
        g_form.addOption('subcategory', 'software_bugs', 'Software Bugs');
        g_form.addOption('subcategory', 'scanning_or_calibration_issues', 'Scanning or Calibration Issues');
    }

    var ga = new GlideAjax('NewValue'); // Scoped Script Include name
    ga.addParam('sysparm_name', 'processDataFromClient');
    ga.addParam('param1', newValue);

    ga.getXML(callbackFunctionScoped);
}

function callbackFunctionScoped(response) {
