function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
	var caller = g_form.getValue("caller_id");
    var gr_ajax = new GlideAjax("x_1129732_healthca.NewScriptInclude");
    gr_ajax.addParam('sysparm_name','getCategory1');
    gr_ajax.addParam('sysparm_user_name',caller);
    gr_ajax.getXML(HelloWorldParse);































// 	var assignmentgroup=g_form.getValue('assignment_group');

	g_form.clearOptions('subcategory');      
	if(newValue=='monitoring_equipment'){
		alert("hello");
		g_form.addOption('subcategory','device_malfunction','Device Malfunction');
				g_form.addOption('subcategory','connection_issues','Connection Issues');
				g_form.addOption('subcategory','alarm_notifications','Alarm Notifications');
				g_form.addOption('subcategory','dac','Data Accuracy Concerns');

	}else if(newValue=='infusion_device'){
		g_form.addOption('subcategory','flow_rate_irregularities','Flow Rate Irregularities');
				g_form.addOption('subcategory','connection_issues','Connection Issues');
				g_form.addOption('subcategory','alarm_notifications','Alarm Notifications');
				g_form.addOption('subcategory','dac','Data Accuracy Concerns');

	}


}


  function HelloWorldParse(response) {
        var answer = response.responseXML.documentElement.getAttribute("answer");
        g_form.setValue("short_description", answer);
	  alert(answer);
    }
